import { request } from "@/plugins/axios/axios"

interface LoginForm {
  tenantId: string
  grant_type: string
  scope: string
  type: string
  username: string
  password: string
}

export const login = (params: LoginForm) => {
  return request({
    url: "/api/blade-auth/oauth/token",
    method: "post",
    params,
  })
}
